import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/auth/signin', { email, password });
      localStorage.setItem('accessToken', response.data.accessToken);
      navigate('/dashboard');
    } catch (error) {
      alert('Credenciales incorrectas');
    }
  };

  return (
    <div className="login-compact-container">
      <div className="login-compact-card">
        <img src="/logo.png" alt="Logo" className="compact-logo" />
        
        <h2 className="compact-title">Acceso al Sistema</h2>
        <p className="compact-subtitle">Gestión de usuarios y permisos</p>

        <form onSubmit={handleSubmit} className="compact-form">
          <div className="form-row">
            <label>Correo electrónico</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="usuario@dominio.com"
              required
            />
          </div>

          <div className="form-row">
            <label>Contraseña</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="••••••••"
              required
            />
          </div>

          <button type="submit" className="compact-button">
            Ingresar
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
